mapper.select:
select user_code 'userId', user_id 'userAcct', user_name 'userName', sex, phone, email 'mail', idcard 'idCard' from user where is_delete = 0 and role = 4
and user_name like concat('%',#{userName},'%')
and user_id = #{userAcct}

select s1.user_id 'userAcct', s1.user_name 'userName', s1.sex, s1.phone, s1.email 'mail', s1.idCard 'idCard' from user s1 inner JOIN store s2 on s1.invite_code = s2.invite_code and s2.user_code = #{userId}
and user_name like concat('%',#{userName},'%')
and user_id = #{userAcct}
