mapper.select:
select
    s1.order_id     'orderId',
    s1.order_price  'orderAllCost',
    s1.order_state  'orderStateId',
    s1.store_id     'storeId',
    s2.user_name    'userName',
    s2.phone        'phone',
    s1.verson       'version',
    s1.pay_time     'payTime'
from order_table s1
inner join user s2
on s1.user_code = s2.user_code
where s1.is_delete = 0
and s2.is_delete = 0
and s2.role = #{role}

    and s2.user_name = #{userName}


    and s2.phone = #{phone}


    and s1.order_id = #{orderId}


    and s1.order_state = #{orderStateId}


    and s1.pay_time_start = #{payTimeStart}


    and s1.pay_time_end = #{payTimeEnd}

mapper.update:
update order_table set order_state = #{orderStateId}, update_user = #{userId}, update_time = now(), verson = verson + 1 where is_delete = 0 and order_id in
#{item}
and verson in
#{item}
