mapper.select:
select sort_num 'slideshowNum', picture_id 'slideshowId', picture_address 'slideshowPath', is_use 'slideshowStateId', start_time 'startTime', close_time 'endTime', verson 'version' from picture where is_delete = 0
and is_use = #{slideshowStateId}
order by sort_num

mapper.update:
update picture set is_use = #{slideshowStateId}, update_user = #{userId}, update_time = now(), verson = verson + 1 where is_delete = 0 and picture_id in
#{item}
and verson in
#{item}
